/**
 * The `Box` class allows users to box a primitive value, allowing them to
 * treat it as nullable (`Box<T> | null`).
 * @internal
 */
export class Box<T> {
  value: T;

  constructor(value: T) {
    assert(!isReference(value), 'Box is useless for reference types');
    this.value = value;
  }
}
