import {Product} from './product';
import {Configuration} from './configuration';
import {registerFunction} from '@shopify/scripts-sdk-as';
import {wrapper} from './serializer';

// The interface and handler are required for codegen
export type EPInterface = (product: Product, myConfig: Configuration) => Product;
export let EPHandler: EPInterface;
export const EPName: string = 'ep1';
export function registerEP1Handler(handler: EPInterface): void {
  EPHandler = handler;
  registerFunction(EPName, wrapper);
}
