import {registerFunction} from '@shopify/scripts-sdk-as';
import {wrapper} from './serializer';
import {Key, Value, MyMap, Flag} from './key';

// The interface and handler are required for codegen
export type EPInterface = (
  number: i8,
  array: Array<Key>,
  map: Map<string, Value>,
  key: Key,
  myMap: MyMap,
  flag: Flag,
  setOfNumber: Set<i8>,
  setOfKey: Set<Key>
) => Set<Key>;
export let EPHandler: EPInterface;
export const EPName: string = 'ep2';
export function registerEP2Handler(handler: EPInterface): void {
  EPHandler = handler;
  registerFunction(EPName, wrapper);
}
