import {registerFunction, Box} from '@shopify/scripts-sdk-as';
import {wrapper} from './serializer';
import {User, Quantity} from './user';

// The interface and handler are required for codegen
export type EPInterface = (user: User, boxOfNumber: Box<Quantity> | null, boxOfBool: Box<bool>) => User;
export let EPHandler: EPInterface;
export const EPName: string = 'ep4';
export function registerEP4Handler(handler: EPInterface): void {
  EPHandler = handler;
  registerFunction(EPName, wrapper);
}
