import {registerEP1Handler} from '../ep1/interface';
import {registerEP2Handler} from '../ep2/interface';
import {registerEP3Handler} from '../ep3/interface';
import {registerEP4Handler} from '../ep4/interface';
import {handleEP1, handleEP2, handleEP3, handleEP4} from './script';
import {run} from '@shopify/scripts-sdk-as';

export function shopify_main(): void {
  registerEP1Handler(handleEP1);
  registerEP2Handler(handleEP2);
  registerEP3Handler(handleEP3);
  registerEP4Handler(handleEP4);
  run();
}
